package com.google.openthread;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

import COSE.Message;
import COSE.MessageTag;
import COSE.OneKey;
import COSE.Sign1Message;
import com.google.openthread.tools.CredentialGenerator;
import java.io.Reader;
import java.io.StringReader;
import java.security.KeyFactory;
import java.security.PrivateKey;
import java.security.PublicKey;
import java.security.cert.X509Certificate;
import java.security.spec.X509EncodedKeySpec;
import org.bouncycastle.util.encoders.Hex;
import org.junit.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class CoseTest {

  private static String TEST_CREDENTIALS_STORAGE_FILE = "temp.credentials.store.p12";
  private static byte[] TEST_CONTENT_BYTES = {1, 2, 3, 4, 5, 6, 50, 60, 70, 80, 90, 100, 110};
  private static Logger logger = LoggerFactory.getLogger(CoseTest.class);

  @Test
  public void testSignAndVerify() throws Exception {
    final String certificate =
        "-----BEGIN CERTIFICATE-----\r\n"
            + "MIICATCCAaegAwIBAgIIJU8KN/Bcw4cwCgYIKoZIzj0EAwIwGDEWMBQGA1UEAwwN\r\n"
            + "VGhyZWFkR3JvdXBDQTAeFw0xOTA2MTkyMTM2MTFaFw0yNDA2MTcyMTM2MTFaMBox\r\n"
            + "GDAWBgNVBAMMD1RocmVhZFJlZ2lzdHJhcjBZMBMGByqGSM49AgEGCCqGSM49AwEH\r\n"
            + "A0IABCAwhVvoRpELPssVyvhXLT61Zb3GVKFe+vbt66qLnhYIxckQyTogho/IUE03\r\n"
            + "Dxsm+pdZ9nmDu3iGPtqay+pRJPajgdgwgdUwDwYDVR0TBAgwBgEB/wIBAjALBgNV\r\n"
            + "HQ8EBAMCBeAwbAYDVR0RBGUwY6RhMF8xCzAJBgNVBAYTAlVTMRUwEwYDVQQKDAxU\r\n"
            + "aHJlYWQgR3JvdXAxFzAVBgNVBAMMDlRlc3QgUmVnaXN0cmFyMSAwHgYJKoZIhvcN\r\n"
            + "AQkBFhFtYXJ0aW5Ac3Rva29lLm5ldDBHBgNVHSMEQDA+gBSS6nZAQEqPq08nC/O8\r\n"
            + "N52GzXKA+KEcpBowGDEWMBQGA1UEAwwNVGhyZWFkR3JvdXBDQYIIc5C+m8ijatIw\r\n"
            + "CgYIKoZIzj0EAwIDSAAwRQIgbI7Vrg348jGCENRtT3GbV5FaEqeBaVTeHlkCA99z\r\n"
            + "RVACIQDGDdZSWXAR+AlfmrDecYnmp5Vgz8eTyjm9ZziIFXPUwA==\r\n"
            + "-----END CERTIFICATE-----\r\n";

    final String privateKey =
        "-----BEGIN PRIVATE KEY-----\r\n"
            + "MIGHAgEAMBMGByqGSM49AgEGCCqGSM49AwEHBG0wawIBAQQgYJ/MP0dWA9BkYd4W\r\n"
            + "s6oRY62hDddaEmrAVm5dtAXE/UGhRANCAAQgMIVb6EaRCz7LFcr4Vy0+tWW9xlSh\r\n"
            + "Xvr27euqi54WCMXJEMk6IIaPyFBNNw8bJvqXWfZ5g7t4hj7amsvqUST2\r\n"
            + "-----END PRIVATE KEY-----\r\n";

    X509Certificate cert;
    PrivateKey key;

    try (Reader reader = new StringReader(certificate)) {
      cert = SecurityUtils.parseCertFromPem(reader);
    }
    try (Reader reader = new StringReader(privateKey)) {
      key = SecurityUtils.parsePrivateKeyFromPem(reader);
    }

    byte[] signature =
        SecurityUtils.genCoseSign1Message(
            key, SecurityUtils.COSE_SIGNATURE_ALGORITHM, TEST_CONTENT_BYTES);
    logger.debug("Encoded COSE message: " + Hex.toHexString(signature));

    Sign1Message msg = (Sign1Message) Message.DecodeFromBytes(signature, MessageTag.Sign1);
    assert (msg.validate(new OneKey(cert.getPublicKey(), null)));
  }

  @Test
  public void testSignAndVerifyUsingCredentialsFile() throws Exception {

    // A) Load credentials from existing .p12 file - generated by OpenSSL scripts.
    CredentialGenerator credGen = new CredentialGenerator();
    credGen.load(CredentialGenerator.CREDENTIALS_FILE_IOTCONSULTANCY);
    Credentials credMasaCa = credGen.getCredentials(CredentialGenerator.MASACA_ALIAS);
    Credentials credPledge = credGen.getCredentials(CredentialGenerator.PLEDGE_ALIAS);

    PrivateKey signingKey = credMasaCa.getPrivateKey(); // MASA CA signs
    OneKey validationKey = new OneKey(credPledge.getCaCertificate().getPublicKey(), null);

    byte[] signedMsg =
        SecurityUtils.genCoseSign1Message(
            signingKey, SecurityUtils.COSE_SIGNATURE_ALGORITHM, TEST_CONTENT_BYTES);
    logger.debug("Encoded COSE message: " + Hex.toHexString(signedMsg));

    Sign1Message msg = (Sign1Message) Message.DecodeFromBytes(signedMsg, MessageTag.Sign1);

    // test that Pledge can validate the MASA CA's COSE object
    assert (msg.validate(validationKey));

    // test that MASA CA can validate its own COSE object
    OneKey validationKey2 = new OneKey(credMasaCa.getPublicKey(), null);
    assert (msg.validate(validationKey2));
  }

  @Test
  public void testStoringCredentialsInFile() throws Exception {

    // try creating fresh credentials, sign data, storing keys, loading keys - then use public key
    // to verify.
    CredentialGenerator credGen = new CredentialGenerator();
    credGen.make(null, null, null, null, null);
    credGen.store(TEST_CREDENTIALS_STORAGE_FILE);
    Credentials credMasaCa = credGen.getCredentials(CredentialGenerator.MASACA_ALIAS);
    PrivateKey signingKey = credMasaCa.getPrivateKey();

    byte[] signedMsg =
        SecurityUtils.genCoseSign1Message(
            signingKey, SecurityUtils.COSE_SIGNATURE_ALGORITHM, TEST_CONTENT_BYTES);
    logger.debug("Encoded COSE message: " + Hex.toHexString(signedMsg));

    // re-load Credentials from file
    credGen = new CredentialGenerator();
    credGen.load(TEST_CREDENTIALS_STORAGE_FILE);
    Credentials credPledge = credGen.getCredentials(CredentialGenerator.PLEDGE_ALIAS);
    credMasaCa = credGen.getCredentials(CredentialGenerator.MASACA_ALIAS);
    OneKey validationKey = new OneKey(credPledge.getCaCertificate().getPublicKey(), null);

    Sign1Message msg = (Sign1Message) Message.DecodeFromBytes(signedMsg, MessageTag.Sign1);

    // test that Pledge can validate MASA's signature
    assert (msg.validate(validationKey));

    // test that MASA itself can validate MASA's signature
    OneKey validationKey2 = new OneKey(credMasaCa.getPublicKey(), null);
    assert (msg.validate(validationKey2));
  }

  /**
   * Test COSE message generated by a 3rd party library - as part of IETF interop.
   *
   * @throws Exception
   */
  @Test
  public void testVerifyThirdPartyCose() throws Exception {
    // example input of COSE with a wrong signature format (DER instead of raw 2*32 bytes)
    final String coseHex =
        "";
    final String publicKeyAsnHex =
        "3059301306072a8648ce3d020106082a8648ce3d0301070342000422a1d9b2d6f6ee31e7f6611351720d8af8a905ba8098bacb62a77fa328c8cd5059075c6a8a517c9832759fff4906282a9eccc2266c67d6e85b3314b715b0976e";

    // example input of COSE with good signature format (no DER)
    final String coseHexPatched =
        "";
    final String coseHex2 =
        "";
    byte[] coseData = Hex.decode(coseHex);
    byte[] coseData2 = Hex.decode(coseHex2);
    byte[] coseDataPatched = Hex.decode(coseHexPatched);
    byte[] publicKeyAsn = Hex.decode(publicKeyAsnHex);

    // logger.info(Hex.toHexString(convertDerToConcat(Hex.decode("3046022100CBDFF5E6270A4F94639CD12FB4D1BE312F5E3E88A44758135082D311A3155FF7022100DA8FD7BC42F1178BD1CE816D8C0FD6F819DD9CF52CF50BAA82252E1DE23FAD6D"), 32)));

    // decode Cose-Sign1 messages
    Sign1Message msg = (Sign1Message) Message.DecodeFromBytes(coseData, MessageTag.Sign1);
    Sign1Message msg2 = (Sign1Message) Message.DecodeFromBytes(coseDataPatched, MessageTag.Sign1);
    coseDataPatched[coseDataPatched.length - 1] += 1; // test: inject wrong signature.
    Sign1Message msg3 = (Sign1Message) Message.DecodeFromBytes(coseDataPatched, MessageTag.Sign1);
    Sign1Message msg2b = (Sign1Message) Message.DecodeFromBytes(coseData2, MessageTag.Sign1);

    // verify both against public key:
    // should not pass, as wrong signature format is used (DER-encoded, instead of raw 2*32 bytes)
    // in COSE
    KeyFactory kf = KeyFactory.getInstance("EC");
    PublicKey pubKey = kf.generatePublic(new X509EncodedKeySpec(publicKeyAsn));
    assertFalse(msg.validate(new OneKey(pubKey, null)));

    // this time good signature
    assertTrue(msg2.validate(new OneKey(pubKey, null)));

    // also a bad example here
    assertFalse(msg3.validate(new OneKey(pubKey, null)));

    assertTrue(msg2b.validate(new OneKey(pubKey, null)));
  }
}
